(function(){tinymce.PluginManager.requireLangPack('cards');tinymce.create('tinymce.plugins.CardsPlugin',{init:function(ed,url,eurl){ed.addCommand('mceCards',function(){var furl=tinyMCE.activeEditor.getParam('plugin_cards_url');ed.windowManager.open({file:url+'/cards.php'+furl,width:350+parseInt(ed.getLang('cards.delta_width',0)),height:150+parseInt(ed.getLang('cards.delta_height',0)),inline:1},{plugin_url:url})});ed.addButton('cards',{title:'cards.desc',image:url+'/img/card.gif',cmd:'mceCards'})},getInfo:function(){return{longname:'Cards',author:'Petr Hota, Edencms.com',authorurl:'http://www.edencms.com',infourl:'http://www.edencms.com',version:tinymce.majorVersion+"."+tinymce.minorVersion}}});tinymce.PluginManager.add('cards',tinymce.plugins.CardsPlugin)})();